patterns:
^# User@Host: %{USER:mysql.slowlog.user}(\[[^\]]+\])? @ (%{HOSTNAME:mysql.slowlog.host})? \[(%{IP:mysql.slowlog.ip})?\]
# Thread_id:\s* %{NUMBER:mysql.slowlog.id}\s* Schema:\s* %{WORD:mysql.slowlog.schema}\s* QC_hit:\s*  %{WORD:mysql.slowlog.qc_hit}
# Query_time: %{NUMBER:mysql.slowlog.query_time.sec}\s* Lock_time: %{NUMBER:mysql.slowlog.lock_time.sec}\s* Rows_sent: %{NUMBER:mysql.slowlog.rows_sent}\s* Rows_examined: %{NUMBER:mysql.slowlog.rows_examined}
# Rows_affected: %{NUMBER:mysql.slowlog.rows_affected}
(# Full_scan: %{WORD:mysql.slowlog.full_scan}\s* Full_join:%{WORD:mysql.slowlog.full_join}\s* Tmp_table: %{WORD:mysql.slowlog.tmp_table}\s* Tmp_table_on_disk: %{WORD:mysql.slowlog.tmp_table_on_disk}
)?(# Filesort: %{WORD:mysql.slowlog.filesort}\s* Filesort_on_disk: %{WORD:mysql.slowlog.filesort_on_disk}\s* Merge_passes: %{NUMBER:mysql.slowlog.merge_passes}\s* Priority_queue: %{WORD:mysql.slowlog.priority_queue}
)?(SET timestamp=%{NUMBER:mysql.slowlog.timestamp};
)?%{GREEDYMULTILINE:mysql.slowlog.query}

^# User@Host: %{USER:mysql.slowlog.user}(\[[^\]]+\])? @ (%{HOSTNAME:mysql.slowlog.host})? \[(%{IP:mysql.slowlog.ip})?\](\s*Id:\s* %{NUMBER:mysql.slowlog.id})?
# Schema:\s* (%{WORD:mysql.slowlog.schema}\s*)? Last_errno:\s* %{NUMBER:mysql.slowlog.last_errno}\s* Killed: %{NUMBER:mysql.slowlog.killed}
# Query_time: %{NUMBER:mysql.slowlog.query_time.sec}\s* Lock_time: %{NUMBER:mysql.slowlog.lock_time.sec}\s* Rows_sent: %{NUMBER:mysql.slowlog.rows_sent}\s* Rows_examined: %{NUMBER:mysql.slowlog.rows_examined}\s* Rows_affected: %{NUMBER:mysql.slowlog.rows_affected}
# Bytes_sent: %{NUMBER:mysql.slowlog.bytes_send}\s* Tmp_tables: %{NUMBER:mysql.slowlog.tmp_tables}\s* Tmp_disk_tables: %{NUMBER:mysql.slowlog.tmp_disk_tables}\s* Tmp_table_sizes: %{NUMBER:mysql.slowlog.tmp_table_size}
(# InnoDB_trx_id: %{WORD:mysql.slowlog.innodb_trx_id}
)?(# Profile_starting: .*
)?(# Profile_total: %{NUMBER:mysql.slowlog.profile_total} Profile_total_cpu: %{NUMBER:mysql.slowlog.profile_total_cpu}
)?# QC_Hit: %{WORD:mysql.slowlog.qc_hit}\s* Full_scan: %{WORD:mysql.slowlog.full_scan}\s* Full_join: %{WORD:mysql.slowlog.full_join}\s* Tmp_table: %{WORD:mysql.slowlog.tmp_table}\s* Tmp_table_on_disk: %{WORD:mysql.slowlog.tmp_table_on_disk}
# Filesort: %{WORD:mysql.slowlog.filesort}\s* Filesort_on_disk: %{WORD:mysql.slowlog.filesort_on_disk}\s* Merge_passes: %{NUMBER:mysql.slowlog.merge_passes}
(# No InnoDB statistics available for this query
)?(#\s* InnoDB_IO_r_ops: %{NUMBER:mysql.slowlog.innodb_r_ops}\s* InnoDB_IO_r_bytes: %{NUMBER:mysql.slowlog.innodb_r_bytes}\s* InnoDB_IO_r_wait: %{NUMBER:mysql.slowlog.innodb_r_wait.sec}
)?(#\s* InnoDB_rec_lock_wait: %{NUMBER:mysql.slowlog.innodb_rec_lock_wait.sec}\s* InnoDB_queue_wait: %{NUMBER:mysql.slowlog.innodb_queue_wait.sec}
)?(#\s* InnoDB_pages_distinct: %{NUMBER:mysql.slowlog.pages_distinct}
)?(# Log_slow_rate_type: %{WORD:mysql.slowlog.slow_rate_type}\s* Log_slow_rate_limit: %{NUMBER:mysql.slowlog.slow_rate_limit}
)?(use %{WORD:mysql.slowlog.use};
)?(SET timestamp=%{NUMBER:mysql.slowlog.timestamp};
)?%{GREEDYMULTILINE:mysql.slowlog.query}
